properties.entry:
prefix gephi:<http://gephi.org/>
CONSTRUCT{
	?src ?relation ?target .
	?relation gephi:label "test".
	?relation gephi:attribute1 "testattribute1".
	?src gephi:firstName ?srcFirstName .
	?src gephi:familyName ?srcFamilyName .
	?src gephi:type ?srcType .
	?target gephi:firstName ?targetFirstName .
	?target gephi:familyName ?targetFamilyName .
	?target gephi:type ?targetType .
} WHERE {
	?src ?relation ?target .
	{
		{ FILTER(?relation = <http://xmlns.com/foaf/0.1/knows>) }
		UNION
		{ ?relation rdfs:subPropertyOf <http://xmlns.com/foaf/0.1/knows> }
	}
	?src foaf:firstName ?srcFirstName .
	?src foaf:familyName ?srcFamilyName .
	?src rdf:type ?srcType .
	?target foaf:firstName ?targetFirstName .
	?target foaf:familyName ?targetFamilyName .
	?target rdf:type         ?targetType .
}
GROUP BY ?src ?target
